// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.7.6;

import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import 'diamond-2/contracts/libraries/LibDiamond.sol';
import '../Registry/lib/LibRegistryProxyStorage.sol';
import '../Registry/interfaces/IRegistryFacet.sol';
import './interfaces/IERC20DepositFacet.sol';
import './lib/LibERC20Storage.sol';
import '../../utils/Access.sol';

contract ERC20DepositFacet is Access, IERC20DepositFacet {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    function depositFrom(address _sender, uint256 _amount) external override {
        require(_amount > 0, 'ZERO_AMOUNT');
        _deposit(_sender, _amount);
    }

    function depositFromMany(address[] memory _senders, uint256[] memory _amounts) external override {
        for (uint256 i = 0; i < _senders.length; i++) {
            _deposit(_senders[i], _amounts[i]);
        }
    }

    function _deposit(address _sender, uint256 _amount) internal {
        LibRegistryProxyStorage.RegistryProxyStorage storage rs = LibRegistryProxyStorage.s();
        LibERC20Storage.ERC20Storage storage s = LibERC20Storage.s();
        IRegistryFacet registry = IRegistryFacet(rs.registry);

        uint256 fee = _amount.mul(registry.feePercentage()).div(10**18);
        uint256 amount = _amount.sub(fee);

        if (fee > 0) {
            s.token.safeTransferFrom(_sender, registry.feeCollector(), fee);
            emit ERC20DepositFeeCollected(fee);
        }

        s.token.safeTransferFrom(_sender, address(this), amount);
        emit ERC20DepositFrom(_sender, amount);
    }
}

contracts/modules/ERC20/ERC20SwapFacet.s.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.7.6;

import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import 'diamond-2/contracts/libraries/LibDiamond.sol';
import '../Registry/lib/LibRegistryProxyStorage.sol';
import '../Registry/interfaces/IRegistryFacet.sol';
import './interfaces/IERC20SwapFacet.sol';
import './lib/LibERC20Storage.sol';
import '../../utils/Access.sol';

contract ERC20SwapFacet is Access, IERC20SwapFacet {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    function setSwapRule(address _tokenAddress, uint256 multiplier) external override onlyOwner {
        LibERC20Storage.s().multipliers[_tokenAddress] = multiplier;
        emit ERC20SwapRuleUpdated(_tokenAddress, multiplier);
    }

    function getSwapRule(address _tokenAddress) external view override returns (uint256) {
        return LibERC20Storage.s().multipliers[_tokenAddress];
    }

    function swapFor(
        address _sender,
        uint256 _amountIn,
        address _tokenAddress
    ) external override onlyOwner {
        LibRegistryProxyStorage.RegistryProxyStorage storage rs = LibRegistryProxyStorage.s();
        LibERC20Storage.ERC20Storage storage s = LibERC20Storage.s();

        IERC20 tokenB = IERC20(_tokenAddress);

        require(tokenB.balanceOf(_sender) >= _amountIn, 'INSUFFICIENT_TOKEN_B_BALANCE');
        require(s.multipliers[_tokenAddress] > 0, 'SWAP_NOT_ALLOWED');

        uint256 amountOut = _amountIn.mul(s.multipliers[_tokenAddress]);
        require(s.token.balanceOf(address(this)) >= amountOut, 'INSUFFICIENT_TOKEN_A_BALANCE');

        IRegistryFacet registry = IRegistryFacet(rs.registry);
        uint256 fee = _amountIn.mul(registry.feePercentage()).div(10**18);
        uint256 amount = _amountIn.sub(fee);

        if (fee > 0) {
            tokenB.transferFrom(_sender, registry.feeCollector(), fee);
            emit ERC20SwapFeeCollected(fee);
        }

        tokenB.safeTransferFrom(_sender, address(this), amount);
        s.token.safeTransfer(_sender, amountOut);

        emit ERC20SwapFor(_sender, _amountIn, amountOut, address(this), _tokenAddress);
    }
}

contracts/modules/ERC20/ERC20ProxyFacet..content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.7.6;

import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import 'diamond-2/contracts/libraries/LibDiamond.sol';
import '../../utils/Access.sol';
import './interfaces/IERC20ProxyFacet.sol';
import './lib/LibERC20Storage.sol';

contract ERC20ProxyFacet is Access, IERC20ProxyFacet {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    function getERC20() external view override returns (address) {
        return address(LibERC20Storage.s().token);
    }

    function setERC20(address _erc20) external override onlyOwner {
        LibERC20Storage.ERC20Storage storage s = LibERC20Storage.s();

        require(s.token == IERC20(0) || s.token == IERC20(_erc20), 'ADDRESS_INIT');
        require(_erc20 != address(0), 'ADDRESS_ZERO');

        uint256 MAX_INT = 2**256 - 1;

        s.token = IERC20(_erc20);
        s.token.approve(address(this), MAX_INT);
        emit ERC20ProxyUpdated(address(0), _erc20);
    }

    function totalSupply() external view override returns (uint256) {
        return LibERC20Storage.s().token.totalSupply();
    }

    function name() external view override returns (string memory) {
        // TODO should change storage struct type for token here, but might cause storage pointer conflicts
        return ERC20(address(LibERC20Storage.s().token)).name();
    }

    function symbol() external view override returns (string memory) {
        // TODO should change storage struct type for token here, but might cause storage pointer conflicts
        return ERC20(address(LibERC20Storage.s().token)).symbol();
    }

    function balanceOf(address _account) external view override returns (uint256) {
        return LibERC20Storage.s().token.balanceOf(_account);
    }

    function transferFrom(
        address _sender,
        address _recipient,
        uint256 _amount
    ) external override onlyOwner {
        require(_amount > 0, 'ZERO_AMOUNT');
        _transferFrom(_sender, _recipient, _amount);
    }

    function transferFromMany(
        address[] memory _senders,
        address[] memory _recipients,
        uint256[] memory _amounts
    ) external override onlyOwner {
        for (uint256 i = 0; i < _recipients.length; i++) {
            _transferFrom(_senders[i], _recipients[i], _amounts[i]);
        }
    }

    function _transferFrom(
        address _sender,
        address _recipient,
        uint256 _amount
    ) internal {
        LibERC20Storage.s().token.safeTransferFrom(_sender, _recipient, _amount);
        emit ERC20ProxyTransferFrom(_sender, _recipient, _amount);
    }
}

