contracts/util/BasePoll.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.7.4;

import '@openzeppelin/contracts/math/SafeMath.sol';

import '../TMP/TMP6/LibBasePollStorage.sol';
import './Access.sol'; // TMP 1

abstract contract BasePoll is Access {
    using SafeMath for uint256;

    modifier isSelf {
        require(msg.sender == address(this));
        _;
    }

    function onPollFinish(uint256 _id) internal virtual;

    function voteValidate(address _voter) internal virtual;

    modifier checkTime() {
        LibBasePollStorage.BasePollStorage storage bData = baseData();
        require(block.timestamp >= bData.startTime && block.timestamp <= bData.endTime, 'IS_NO_VALID_TIME');
        _;
    }

    /**
     * @notice Finalize poll and call onPollFinish callback with result
     */
    function finalize() internal {
        LibBasePollStorage.BasePollStorage storage bData = baseData();
        require(block.timestamp >= bData.endTime, 'WRONG_STATE');
        onPollFinish(bData.id);
        delete bData.id;
        delete bData.startTime;
        delete bData.endTime;
        delete bData.yesCounter;
        delete bData.noCounter;
        delete bData.totalVoted;
        //delete bData.votesByAddress;
    }

    /**
     * @dev callback called after poll finalization
     * @param _agree True if user endorses the proposal else False
     */
    function vote(bool _agree) internal checkTime {
        voteValidate(_msgSender());
        LibBasePollStorage.BasePollStorage storage bData = baseData();

        require(bData.votesByAddress[_msgSender()].time == 0, 'HAS_VOTED');
        uint256 voiceWeight = 1;

        if (_agree) {
            bData.yesCounter = bData.yesCounter.add(voiceWeight);
        } else {
            bData.noCounter = bData.noCounter.add(voiceWeight);
        }

        bData.votesByAddress[_msgSender()].time = block.timestamp;
        bData.votesByAddress[_msgSender()].weight = voiceWeight;
        bData.votesByAddress[_msgSender()].agree = _agree;

        bData.totalVoted = bData.totalVoted.add(1);
    }

    /**
     * @notice Revoke user`s vote
     */
    function revokeVote() internal checkTime {
        LibBasePollStorage.BasePollStorage storage bData = baseData();
        address _voter = _msgSender();

        require(bData.votesByAddress[_voter].time > 0, 'HAS_NOT_VOTED');

        uint256 voiceWeight = bData.votesByAddress[_voter].weight;
        bool agree = bData.votesByAddress[_voter].agree;

        bData.votesByAddress[_voter].time = 0;
        bData.votesByAddress[_voter].weight = 0;
        bData.votesByAddress[_voter].agree = false;

        bData.totalVoted = bData.totalVoted.sub(1);
        if (agree) {
            bData.yesCounter = bData.yesCounter.sub(voiceWeight);
        } else {
            bData.noCounter = bData.noCounter.sub(voiceWeight);
        }
    }

    function baseData() internal pure returns (LibBasePollStorage.BasePollStorage storage) {
        return LibBasePollStorage.basePollStorage(bps());
    }

    /**
     * @dev This function gets the poll ID of message data that is appended with an address
     */
    function bps() internal pure returns (bytes32 rt) {
        // These fields are not accessible from assembly
        bytes memory array = msg.data;
        // minus address space
        uint256 index = msg.data.length - 20;

        // solhint-disable-next-line no-inline-assembly
        assembly {
            rt := mload(add(array, index))
        }
    }
}

contracts/util/Access.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.7.4;

/******************************************************************************\
* @title Access
* @author Evert Kors <evert@thx.network>
* @notice Implement role-based access control.
* 
* @dev
* Implementations: 
* TMP-1 Access Control: https://github.com/thxprotocol/modules/issues/1
/******************************************************************************/

import '@openzeppelin/contracts/utils/EnumerableSet.sol';
import '@openzeppelin/contracts/utils/Address.sol';
import 'diamond-2/contracts/libraries/LibDiamond.sol';

// depends on
import '../TMP/TMP1/IAccessControlEvents.sol';
import '../TMP/TMP1/LibAccessStorage.sol';

import '../TMP/RelayReceiver.sol';

contract Access is RelayReceiver, IAccessControlEvents {
    //
    // Access control view methods internal
    //
    using EnumerableSet for EnumerableSet.AddressSet;
    using Address for address;

    modifier onlyOwner() {
        require(LibDiamond.contractOwner() == _msgSender(), 'NOT_OWNER');
        _;
    }

    modifier onlyManager() {
        require(_hasRole(MANAGER_ROLE, _msgSender()), 'NOT_MANAGER');
        _;
    }

    modifier onlyMember() {
        require(_hasRole(MEMBER_ROLE, _msgSender()), 'NOT_MEMBER');
        _;
    }

    /**
     * @dev Checks if account bears role.
     * @param role Bytes32 array representing the role.
     * @param account Address of the account
     * @return if role member storage array contains the given account address.
     */
    function _hasRole(bytes32 role, address account) internal view virtual returns (bool) {
        LibAccessStorage.RoleStorage storage rs = LibAccessStorage.roleStorage();

        return rs.roles[role].members.contains(account);
    }

    /**
     * @dev Called in initialize methods.
     * @param role Bytes32 array representing the role.
     * @param account Address of the account
     */
    function _setupRole(bytes32 role, address account) internal virtual {
        _grantRole(role, account);
    }

    /**
     * @dev Called during initialization
     * @param role Bytes32 array representing the role.
     * @param adminRole Address of the role admin
     */
    function _setRoleAdmin(bytes32 role, bytes32 adminRole) internal virtual {
        LibAccessStorage.RoleStorage storage rs = LibAccessStorage.roleStorage();

        emit RoleAdminChanged(role, rs.roles[role].adminRole, adminRole);
        rs.roles[role].adminRole = adminRole;
    }

    /**
     * @param role Bytes32 array representing the role.
     * @param account Address of the account
     */
    function _grantRole(bytes32 role, address account) internal virtual {
        LibAccessStorage.RoleStorage storage rs = LibAccessStorage.roleStorage();

        if (rs.roles[role].members.add(account)) {
            emit RoleGranted(role, account, _msgSender());
        }
    }

    /**
     * @param role Bytes32 array representing the role.
     * @param account Address of the account
     */
    function _revokeRole(bytes32 role, address account) internal virtual {
        LibAccessStorage.RoleStorage storage rs = LibAccessStorage.roleStorage();

        if (rs.roles[role].members.remove(account)) {
            emit RoleRevoked(role, account, _msgSender());
        }
    }

    //
    // Pool roles view methods internal
    //
    bytes32 internal constant DEFAULT_ADMIN_ROLE = 0x00;
    bytes32 internal constant MEMBER_ROLE = keccak256('MEMBER_ROLE');
    bytes32 internal constant MANAGER_ROLE = keccak256('MANAGER_ROLE');

    function _isManager(address _account) internal view returns (bool) {
        return _hasRole(MANAGER_ROLE, _account);
    }

    function _isMember(address _account) internal view returns (bool) {
        return _hasRole(MEMBER_ROLE, _account) || _hasRole(MANAGER_ROLE, _account);
    }

    function _getOwner() internal view returns (address) {
        return LibDiamond.contractOwner();
    }
}

contracts/06-Reward/RewardPoll.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

/******************************************************************************\
* @title Reward Poll
* @author Evert Kors <evert@thx.network>
* @notice Extends base polls with reward information.
* 
* Implementations:
* TMP-6 Base poll: https://github.com/thxprotocol/modules/issues/6
* TMP-8 Rewards: https://github.com/thxprotocol/modules/issues/8
/******************************************************************************/

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/utils/EnumerableSet.sol';
import '@openzeppelin/contracts/utils/Address.sol';
import 'diamond-2/contracts/libraries/LibDiamond.sol';

// Implements
import '../util/BasePoll.sol'; // TMP1, TMP 6
import '../TMP/TMP6/LibBasePollStorage.sol';
import '../TMP/TMP8/IRewardPoll.sol';
import '../TMP/TMP8/LibRewardPollStorage.sol';

contract RewardPoll is BasePoll, IRewardPoll {
    uint256 constant ENABLE_REWARD = 2**250;
    uint256 constant DISABLE_REWARD = 2**251;

    modifier isReward {
        LibBasePollStorage.BasePollStorage storage bData = baseData();

        LibRewardPollStorage.RewardPollStorage storage rwPollData = LibRewardPollStorage.rewardPollStorageId(bData.id);

        require(rwPollData.withdrawAmount != 0, 'NOT_REWARD_POLL');
        _;
    }

    function voteValidate(address _voter) internal view override {
        require(_isMember(_voter), 'NO_MEMBER');
    }

    /**
     * @dev callback called after poll finalization
     * @param _id ID of the reward poll
     */
    function onPollFinish(uint256 _id) internal override {
        LibRewardPollStorage.RewardPollStorage storage rwPollData = LibRewardPollStorage.rewardPollStorageId(_id);

        LibRewardPollStorage.Reward storage reward =
            LibRewardPollStorage.rewardStorage().rewards[rwPollData.rewardIndex];

        bool approved = _rewardPollApprovalState();
        if (approved) {
            if (rwPollData.withdrawAmount == ENABLE_REWARD) {
                reward.state = LibRewardPollStorage.RewardState.Enabled;
                emit RewardPollEnabled(_id);
            } else if (rwPollData.withdrawAmount == DISABLE_REWARD) {
                reward.state = LibRewardPollStorage.RewardState.Disabled;
                emit RewardPollDisabled(_id);
            } else {
                // initial state
                if (reward.withdrawAmount == 0 && reward.withdrawDuration == 0) {
                    reward.state = LibRewardPollStorage.RewardState.Enabled;
                    emit RewardPollEnabled(_id);
                }
                reward.withdrawAmount = rwPollData.withdrawAmount;
                reward.withdrawDuration = rwPollData.withdrawDuration;
                emit RewardPollUpdated(_id, reward.withdrawAmount, reward.withdrawDuration);
            }
        }
        emit RewardPollFinalized(_id, approved);
        delete reward.pollId;
        delete rwPollData.withdrawAmount;
        delete rwPollData.withdrawDuration;
    }

    /**
     * @param _id ID of the reward
     * @return The amound a member can withdraw for this reward.
     */
    function getWithdrawAmount(uint256 _id) external view override returns (uint256) {
        return LibRewardPollStorage.rewardPollStorageId(_id).withdrawAmount;
    }

    /**
     * @param _id ID of the reward
     * @return The duration of the optional withdraw poll for this reward.
     */
    function getWithdrawDuration(uint256 _id) external view override returns (uint256) {
        return LibRewardPollStorage.rewardPollStorageId(_id).withdrawDuration;
    }

    /**
     * @param _id ID of the reward
     * @return The storage index of the reward.
     */
    function getRewardIndex(uint256 _id) external view override returns (uint256) {
        return LibRewardPollStorage.rewardPollStorageId(_id).rewardIndex;
    }

    /**
     * @param _agree Bool reflecting vote for the poll
     * @dev calls generic vote() function and emits reward poll specific event.
     */
    function _rewardPollVote(bool _agree) external override isReward isSelf {
        vote(_agree);
        emit RewardPollVoted(baseData().id, _msgSender(), _agree);
    }

    /**
     * @dev calls abstract revokeVote() function and emits reward poll specific event.
     */
    function _rewardPollRevokeVote() external override isReward isSelf {
        revokeVote();
        emit RewardPollRevokedVote(baseData().id, _msgSender());
    }

    function _rewardPollFinalize() external override isReward isSelf {
        finalize();
    }

    function _rewardPollApprovalState() public view virtual override isReward isSelf returns (bool) {
        LibBasePollStorage.BasePollStorage storage bData = baseData();
        return bData.yesCounter > bData.noCounter;
    }
}

